const Carousel = () => {
  const [currentSlide, setCurrentSlide] = useState(1);
  const [isSlideshowActive, setIsSlideshowActive] = useState(false);

  useEffect(() => {
    if (isSlideshowActive) {
      const interval = setInterval(() => {
        goToNextSlide();
      }, 3000);

      return () => {
        clearInterval(interval);
      };
    }
  }, [isSlideshowActive]);

  const goToPreviousSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 1 ? images.length : prevSlide - 1
    );
  };

  const goToNextSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === images.length ? 1 : prevSlide + 1
    );
  };

  const startSlideshow = () => {
    setIsSlideshowActive(true);
  };

  const stopSlideshow = () => {
    setIsSlideshowActive(false);
  };

  const selectSlide = (slideNumber) => {
    setCurrentSlide(slideNumber);
    if (isSlideshowActive) {
      stopSlideshow();
    }
  };

  const renderThumbnails = () => {
    return images.map((image, index) => (
      <img
        key={index}
        src={image.src}
        alt={image.alt}
        className={`thumbnail ${currentSlide === index + 1 ? "active" : ""}`}
        onClick={() => selectSlide(index + 1)}
      />
    ));
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={9}>
        <img
          src={images[currentSlide - 1].src}
          alt={images[currentSlide - 1].alt}
        />
        <Typography variant="body1" mt={-22} ml={40}>
          {images[currentSlide - 1].details}
        </Typography>
      </Grid>
      <Grid item xs={5}>
        <Grid container direction="row" alignItems="center">
          <Button variant="contained" onClick={goToPreviousSlide}>
            Previous
          </Button>
          <Button
            variant="contained"
            onClick={isSlideshowActive ? stopSlideshow : startSlideshow}
          >
            {isSlideshowActive ? "Pause" : "Play"}
          </Button>
          <Button variant="contained" onClick={goToNextSlide}>
            Next
          </Button>
          <div
            className="thumbnails"
            style={{ display: "flex", flexDirection: "row" }}
          >
            {renderThumbnails()}
          </div>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Carousel;
